import React, { useEffect, useState } from 'react'
import { useAppDispatch, useAppSelector } from '../../../app/hooks';
import { getCustomer } from '../../../features/Customer/Customer';

function Customer() {

  const user = useAppSelector((state) => state.getSlice);
  // console.log('user is' , user.users[0].customer.name);

  const [userDeleted, setuserDeleted] = useState<boolean>(false)

  const dispatch = useAppDispatch();
  useEffect(() => {
    dispatch(getCustomer());
  }, [userDeleted]);

  // const deleteUser = async (id: string) => {
  // await axios.delete(`http://localhost:8001/users/${id}`)
  //  .then((response: any) => toast.success( response.data.message))
  //  .catch((error:Error) => toast.warning( error.message))
  //  setuserDeleted(true)
  // }

  return (
    <div className='container mt-5 mx-auto w-75'>
        <h2 className='text-center'>Customers</h2>
        <table className="table mx-auto w-75">
  <thead className="thead-dark">
    <tr>
      <th scope="col">id</th>
      <th scope="col">Name</th>
      <th scope="col">Website</th>
      <th scope="col">Address</th>
      <th scope="col">Actions</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <th scope="row">1</th>
      <td>Mark</td>
      <td>Otto</td>
      <td>@mdo</td>
      <td>
        <button className='btn btn-primary'>Edit</button>
        <button className='btn btn-danger mx-3'>Delete</button>
        <button className='btn btn-success'>Show Users</button>
      </td>
    </tr>
    
  </tbody>
</table>


    </div>
  )
}

export default Customer
